INSERT INTO `books` (`id`, `id_category`, `number`, `img_path`, `title`, `text`, `status`) VALUES
(1, 1, 1, 'books/wedding.gif', 'Фотоальбом "Ты моя единственная любовь"', 'Свадьба. Это пожалуй самое значимое событие в жизни человека. Это древний обряд, сопровождающийся обменом подарками, праздничным пиром и увеселениями. Конечно же, каждая молодая семья желает сохранить счастливые мгновения заключения брака. Мы предлагаем сделать это оригинально и удобно.  Ваши фотографии не будут лежать кучей в пакете, а превратятся в красивый и презентабельный фотоальбом, который станет отличным подарком на годовщину свадьбы ("золотую", "серебрянную"), или к самому событию.  Изготовление такого альбома потребует от Вас только предоставления фотоснимков в печатном или электронном виде, и затем дизайнер подготовит макет и после Вашего одобрения, через несколько дней, получите художественно оформленную книгу.  Альбом может иметь до ста полноцветных страниц, твердую обложку (возможна и суперобложка), количество экземпляров - до 7 штук (а больше наверное и не надо). Стоимость одного фотоальбома составит от $200. Но это не значит, что 10 штук будут стоить в 10 раз дороже.  Посмотреть пример фотоальбома можно здесь, или по клику на обложку слева.', 'on'),
(2, 1, 2, 'books/strana.gif', 'Фотоальбом "Страна, которой нет"', '<em>2007г., 256 стр., формат 60х90/8, ЗАО "Книга", Киев</em><br>Этот фотоальбом посвящен тем, кто родился и вырос в СССР, кто помнит все или желает вспомнить. Это альбом-ностальгия, дающий возможность увидеть с помощью фотографии всем желающим то, чем жили "рожденные в СССР", что окружало их в детстве, юности и т.д. Общую тематику фотоальбома можно выразить фразой "А помните…" Ностальгия… Как много в этом слове…Это особое чувство, сродни тоске, грусти, это воспоминания о том, что было, но ушло безвозвратно.', 'on'),
(3, 1, 3, 'books/kadenyk.png', 'Фотоальбом "Леонід Каденюк: я твій син, Україно"', '<em>1998г., __ стр., формат _х_/_, АТ "Книга", Киев</em><br>\r\nФотоальбом рассказывает о первом космонавте независимой Украины Леониде Каденюке, который 19 ноября 1997 года вместе с четырьмя астронавтами США и представителем Японии стартовал из мыса Канаверал (США) в космос на космическом корабле "Шаттл".\r\n\r\nНаполеон Бонапарт в своё время отметил: поражение в армии - это беда; утрата императора - двойная беда; утрата национального интеллекта - это полная катастрофа. Теперь, когда Украина переживает сложные времена, экономические неурядицы, утрата национального интеллекта становится реальной угрозой. Тем более символичной становится появление на орбите новой Украины Леонида Каденюка - Космонавта и Человека. Он достойно представляет интеллект нации как представитель военной, научной, технической элиты.', 'on'),
(4, 1, 4, 'books/chernom.png', 'Фотоальбом "Ты знаешь край, где нивы золотые, испещрены лазурью васильков... Черномырдин и Украина"', '<em>2006г., __ стр., формат _х_/_, АТ "Книга", Киев</em><br>\r\nФотоальбом рассказывает о Викторе Черномырдине, который вот уже пять лет работает специальным представителем Президента России и Послом Российской Федерации в Украине.\r\nЭтот неординарный человек начинал карьеру простым слесарем в российской глубинке, но со временем его фамилия стала значится на двери Председателя Правительсва России, а в течение нескольких часов он исполнял обязанности Президента России.\r\nФотоальбом содержит большое количество материалов, посвященных непростым отношениям наших стран и их лидеров, и показывает Виктора Черномырдина в различных жизненных ситуациях.', 'on'),
(5, 1, 5, 'books/kiev_post.png', 'Фотоальбом "Кіевъ на почтовой открытке конца ХІХ начала ХХ века"', '<em>2005г., 160 стр., формат 70х100 1/8, ЗАО "Книга", Киев</em><br>Маленькая почтовая карточка - "открытое письмо", ставшая в нашей повседневной жизни обычным явлением, сто лет назад только входила в мировую культуру. Вслед за гениальным изобретением человечества - фотографией, иллюстрированная открытка многим людям дала возможность познакомиться с жизнью и культурой далеких стран, раздвинула рамки кабинетного познания, позволив каждому желающему иметь у себя дома коллекцию миниатюрных воспроизведений лучших образцов искусства.\r\n\r\nЭтот фотоальбом, оформленный в стиле домашних альбомов наших бабушек собрал открытки с видами Киева конца ХІХ - начала ХХ веков. ', 'on'),
(6, 1, 6, 'books/kievTT.png', 'Фотоальбом "Киевъ вчора і сьогодні"', '<em>2002г., 224 стр., формат 62х94/16, Издательство "Ракурс-Артограф", Мюнхен - Киев - Москва</em><br>Фотоальбом, предлагаемый вниманию читателя, показывает Киев в начале и в конце ХХ века. Благодаря специальному размещению изображений, читатель видит одну и ту же местность, но с разницей почти в 100 лет.  Уникальные архивные фотографии и неоднозначность современного Киева увлекают и затягивают, позволяя читателю совершить путешествие во времени.  Альбом отлично подойдет в качестве сувенира или оригинального подарка для людей, которые интересуются историей или архитектурой матери городов русских. К альбому прилагается вкладка с переводом текста на английский, французский, немецкий, испанский языки.', 'on'),
(7, 1, 7, 'books/kuchma1.png', 'Фотоальбом "Леонід Кучма - Президент України"', '<em>1998г., __ стр., формат _х_/_, АТ "Книга", Киев</em><br>\r\nПройдут годы, и события, с которых начался отсчет независимости Украины, войдут в учебники истории одним из феноменов национального возрождения. Но история не бывает безлична. Потомкам она остается достоянием деяний конкретных людей. Нам, современникам Леонида Кучмы, трудно во всей глубине осознать роль его личности, столь очевидно повлиявший на ход событий. Но взглянуть на них с высоты только лишь первого прожитого в условиях независимости десятилетия - наше право. Мир полон самых противоречивых мнений об Украине и его лидере. Кому, как не нам, живущим на этой земле, виднее, что происходит на ней?\r\n\r\nФотоальбом рассказывает о деятельности Леонида Кучмы на посту Президента Украины и издан в честь 60-ти-летия Президента', 'on'),
(8, 1, 8, 'books/kuchma2.png', 'Фотоальбом "Леонід Кучма"', '<em>2003г., __ стр., формат _х_/_, АТ "Книга", Киев</em><br>\r\nФотоальбом, изданный в честь 65-ти-летия Леонида Кучмы продолжает рассказ о его деятельности как Президента независимой Украины так и общественно-политической деятеля, активной благотворительной деятельности. Фотоальбом на украинском и английском языках.\r\n', 'on'),
(9, 1, 9, 'books/livia.png', 'Фотоальбом "Ливия"', '<em>1999г., __ стр., формат _х_/_, АТ "Книга", Киев</em><br>\r\nЛивия - это настоящая архитектурная жемчужина Средиземноморья. Археологические данные показывают, что ещё 8000 лет до нашей эры на территории Ливии существовали культуры неолита.\r\nСегодня, Ливию многие знают как достаточно опасную и "неудобную" страну, т.к. еще в 1980-е годы под руководством лидера ливийской революции полковника Муаммара Каддафи, пользуясь высокими ценами на нефть, Ливия на себя финансирование вооружённой борьбы против «западного империализма» по всему миру.\r\nПредлагаемый фотоальбом позволит читателю самому сделать выводы и проникнуться по настоящему древней красотой этой Джамахирии.', 'on'),
(10, 1, 10, 'books/mal_ukr.png', 'Фотоальбом "Мальовнича Україна"', '<em>1997г., 192 стр., формат 30х50/8, АТ "Книга", Киев</em><br> На страницах этого издания, с помощью фотографий, воссоздан зрительный образ живописной Украины - от Северного Донца до заснеженных карпатских вершин, от подеснянского Полесья до солнечных берегов Черного моря.\r\n\r\nЯркие и красочные фотографии различных уголков нашей страны покажут читателю все разнообразие и великолепие, данное нам природой.', 'on'),
(11, 1, 11, 'books/mariin.png', 'Фотоальбом "Маріїнський палац у Києві"', '<em>2005г., __ стр., формат _х_/_, АТ "Книга", Киев</em><br>\r\nКак известно, Мариинский дворец — памятник архитектуры ХVIII—ХIХ столетий стиля барокко. Строительство дворца началось в 1752 году по желанию империатрицы Елизаветы Петровны, которая собиралась построить в Киеве свою летнюю резиденцию.\r\n\r\nВ планировке интерьеров и создании сада принимала участие императрица Мария Александровна, в честь которой и был назван дворец.\r\nДля создания фотоальбому было получено специальное разрешение на проведение съемок, и теперь каждый желающий сможет оценить всю роскошь и красоту бывшей императорской резиденции. ', 'on'),
(12, 1, 12, 'books/oldkiev.png', 'Фотоальбом "Старый Киев"', '<em>2005г., 312 стр., формат 62х94/8, Издательский дом "Перископ", Киев</em><br>Киев. Это краткое слово, за которым стоит тысячелетняя история. Ученые и  искусствоведы тщательным образом изучают каждую страницу его летописи.  Но есть еще одна категория исследователей - художники и фотографы.  Именно они смогли сохранить для нас живые цвета, неповторимую атмосферу  города, искренние улыбки горожан - все то, чего не найти в трудах ученых  мужей. Более чем 700 уникальных фотографий этого альбома является  своеобразной захватывающей экскурсией в прошлое, в Киев конца ХIХ века. Этот альбом позволяет увидеть Киев таким, каким он был больше 100 лет назад. Улицы. Газеты. Трамваи. Люди. Церкви. Билеты. Программки. Авто. Мода. Парки. Издание состоит из 8 исторических частей.  Автор с помощью фотографии живо воскрешает прошлое города на Днепре, описывая быт, нравы, культуру, обычаи его жителей, дает фотографии отдельных известных личностей. Альбом напечатан на специальной, искусственно состаренной бумаге, которая помогает проникнутся духом старины и величия древнего Киева.', 'on'),
(13, 1, 13, 'books/war.png', 'Фотоальбом "Вікопомний подвиг"', '<em>2006г., 216 стр., формат 60х90/8, Издательство "Феникс", Киев</em><br>Языком фотографии, плакатов и кадров кинохроники издание рассказывает о Украине в годы Второй мировой войны и посвящен сынам и дочкам всех народов Советского народа, которые защищали Советскую Украину от немецко-фашистских захватчиков. В фотоальбоме опубликован ряд материалов, ранее неиспользованных в других изданиях.\r\nИспользованы плакаты и листовки времен Великой Отечественной войны из Книжной палаты Украины, а также фотографии из Центрального государственного кинофотофоноархива Украины имени Г.Пшеничного, из Центрального государственного архива высших органов власти и управления Украины, из Национальной библиотеки Баварии (Мюнхен, Германия), из фотоальбомов немцев, взятых в плен в годы Великой Отечественной войны. Использованы также трофейные фотографии и документы. ', 'on'),
(14, 1, 14, 'books/shevch.png', 'Фотоальбом "Київський національний університет імені Тараса Шевченка"', '<em>2004г., 224 стр., формат 60х90/8, ЗАО "Книга", Киев</em><br> Фотоальбом рассказывает о гордости украинского образования - Киевском Национальном университете имени Тараса Шевченко.\r\nПоявление этой нарядной книги - свидетельство большого уважения и любви современного поколения сотрудников университета к своим предшественникам, к своему Храму науки, образования, культуры. Это символ благодарности всем тем, кто в течение 170-ти лет создавал бессмертную славу ведущего Университета Украины, а также указатель для ученых третьего тысячелетия. ', 'on'),
(15, 1, 15, 'books/tak.png', 'Фотоальбом "Так, Україно!"', '<em>2005г., 192 стр., формат 70х100/8, АТ "Книга", Киев</em><br> Фотолетопись Оранжевой революции в Украине - кампании протестов, митингов, пикетов, забастовки и других актов гражданского неповиновения в Украине, организованная и проведенная сторонниками Виктора Ющенко, основного кандидата, от оппозиции на президентских выборах в ноябре&nbsp;&mdash; декабре 2004 года, после объявления Центральной избирательной комиссией предыдущих результатов, согласно которым победил его соперник&nbsp;&mdash; Виктор Янукович. Акция началась 22 ноября 2004, как реакция на массовую фальсификацию, которая повлияла на результат выборов. Этот альбом сохранит будущим поколениям фотолетопись тех дней, когда народ Украины для защиты своих демократических прав собрался на главной площади в Киеве и на протяжении двух месяцев защищал свое право голоса и отстаивали справедливость.', 'on'),
(27, 1, 16, 'books/kievxx.png', 'Киев в ХХ веке', '<em>2008г., 536 стр., формат 60х90/8, ЗАО "Книга", Киев</em><br />Этот ретроальбом представляет летопись в фотографиях. книга с помощью редких фотографий рассказывает о наиболее важных и интересных событиях XX века, произошедших в Киеве. Более 2500 иллюстраций, фотографий, плакатов, репродукций и кадров кинохроники представляют историю Киева. Она будет интересна всем, кто хочет узнать историю столицы Украины за последние сто лет, кто хочет расширить свой кругозор, совершить витруальную экскурсию по улицам Киева прошлого века. Прошлое возвратить невозможно - его можно вспомнинать и сохранить.</p>', 'on');
INSERT INTO `category` (`id`, `cat_name`, `part_name`, `text_short`, `text_long`) VALUES
(1, 'books', '', '<p>У всего есть своя история &ndash; у эпохи, у страны, у семьи и у отдельного человека. И фиксировать события своей жизни начали еще древние люди, делая первые наскальные рисунки. Потом создавали рукописные летописи, затем появились первые книги. Но только с появлением фотографии мы получили возможность увидеть неповторимую атмосферу прошлого, детали времени, которые не ускользнули от фотокамеры, искренние улыбки людей,  - все то, чего не найти в трудах ученых мужей. Фотографии являются своеобразной захватывающей экскурсией в прошлое, они своим механическим способом схватывания действительности столько лет со дня своего рождения говорили нам: &laquo;Было! Было. Было&hellip;&raquo; Эти кусочки &ndash; отдельные фотографии, как элементы в мозаичной картине, собирают мир, которого уже нет, но который запечатлен в них. Фотография &ndash; это язык и способ преодоления небытия. Как известно, жизнь скоротечна, и то, что сегодня нам кажется вечным и незыблемым, завтра становится фантомом. А фотография помогает людям мысленно вернуться в прошлое. Бессмертие действительно существует там: в новом мире, созданном в момент нажатия на кнопку фотоаппарата. А зритель, вновь и вновь разглядывающий картинку, закрепляет это бессмертие собой, своим восприятием. Фотография &ndash; способ рождения бессмертия&hellip; На фотографии в ту долю секунды, которая запечатлена на снимке, бессмертны люди, предметы, события...</p>\n<p>Из десятков и сотен обрывочных фото-образов длиной в долю секунды можно сложить целую своеобразную ленту жизни. Именно этим и интересны и знамениты книги и фотоальбомы Ашота. Он занялся издательским делом в начале 90-х, когда в Украине почти не печатали новых книг. Первой ласточкой его издательской деятельности был сборник &laquo;Мальовнича Украина&raquo;, которую отпечатал в Мюнхене &ndash; шикарное красочное издание. Его работа понравилась, и поступили заказы с высот Печерских холмов. Сделал две книги для Кучмы, выпустил фотоальбом для Туркменбаши, потом - фотоальбом для Черномырдина. Он гордится тем, что делал книги первым лицам государства.</p>\n<p>Но больше всего он увлечен историей Киева &ndash; в своих фотоальбомах он хочет показать город, с которым связала его судьба, в котором происходило его формирование как творческой личности. Любовь к старому Киеву Ашот передал в своих работах &ndash; ностальгических альбомах, посвященных Великой Отечественной войне, сравнительном альбоме памятных мест прошлого Киева и современности, альбоме о Киеве конца XIX века. В фотоальбомах и книгах художника собраны уникальные архивные фотоматериалы. Они интересны тем, кто увлекается историей и архитектурой, и тем, кому интересно увидеть старый Киев, который мы уже безвозвратно потеряли. Эти издания и для молодых &ndash; чтобы знали, как люди жили раньше, и для людей "постарше" &ndash; чтобы могли полистать альбом, вспомнить и рассказать своим детям и внукам о том, как это было&hellip;</p>', 'У всего есть своя история – у эпохи, у страны, у семьи и у отдельного человека. И фиксировать события своей жизни начали еще древние люди, делая первые наскальные рисунки. Потом создавали рукописные летописи, затем появились первые книги. Но только с появлением фотографии мы получили возможность увидеть неповторимую атмосферу прошлого, детали времени, которые не ускользнули от фотокамеры, искренние улыбки людей,  - все то, чего не найти в трудах ученых мужей. Фотографии являются своеобразной захватывающей экскурсией в прошлое, они своим механическим способом схватывания действительности столько лет со дня своего рождения говорили нам: «Было! Было. Было…» Эти кусочки – отдельные фотографии, как элементы в мозаичной картине, собирают мир, которого уже нет, но который запечатлен в них. Фотография – это язык и способ преодоления небытия. Как известно, жизнь скоротечна, и то, что сегодня нам кажется вечным и незыблемым, завтра становится фантомом. А фотография помогает людям мысленно вернуться в прошлое. Бессмертие действительно существует там: в новом мире, созданном в момент нажатия на кнопку фотоаппарата. А зритель, вновь и вновь разглядывающий картинку, закрепляет это бессмертие собой, своим восприятием. Фотография – способ рождения бессмертия… На фотографии в ту долю секунды, которая запечатлена на снимке, бессмертны люди, предметы, события... <br>\r\nИз десятков и сотен обрывочных фото-образов длиной в долю секунды можно сложить целую своеобразную ленту жизни. Именно этим и интересны и знамениты книги и фотоальбомы Ашота. Он занялся издательским делом в начале 90-х, когда в Украине почти не печатали новых книг. Первой ласточкой его издательской деятельности был сборник «Мальовнича Украина», которую отпечатал в Мюнхене – шикарное красочное издание. Его работа понравилась, и поступили заказы с высот Печерских холмов. Сделал две книги для Кучмы, выпустил фотоальбом для Туркменбаши, потом - фотоальбом для Черномырдина. Он гордится тем, что делал книги первым лицам государства. <br>\r\nНо больше всего он увлечен историей Киева – в своих фотоальбомах он хочет показать город, с которым связала его судьба, в котором происходило его формирование как творческой личности. Любовь к старому Киеву Ашот передал в своих работах – ностальгических альбомах, посвященных Великой Отечественной войне, сравнительном альбоме памятных мест прошлого Киева и современности, альбоме о Киеве конца XIX века. В фотоальбомах и книгах художника собраны уникальные архивные фотоматериалы. Они интересны тем, кто увлекается историей и архитектурой, и тем, кому интересно увидеть старый Киев, который мы уже безвозвратно потеряли. Эти издания и для молодых – чтобы знали, как люди жили раньше, и для людей "постарше" – чтобы могли полистать альбом, вспомнить и рассказать своим детям и внукам о том, как это было…'),
(2, 'painting', 'portrets', '<p><strong>Портрет с использованием фотоснимков</strong> - одно из актуальных направлений в современном портретном искусстве, которое успешно освоил художник Ашот Арутюнян. В искусствоведении не существует подобного жанрового определения.  Однако, в творчестве художников, особенно портретистов, сегодня данное  тематическое направление реально существует и пользуется большим спросом  у заказчиков. А термин <strong>"портрет-сюрприз" </strong>- изобретение Ашота Арутюнян. Что же это такое?</p>\n<p>Классическая портретная живопись и искусство фотографии в последние годы становятся конкурентными. Что лучше передаст образ человека: фотокамера, документально передающая внешний облик человека, или портретист, иногда весьма субъективно трактующий внешний и внутренний мир модели? Одно не исключает другого, и многие предпочитают видеть свой образ, изображенный красками на холсте в прекрасной картинной раме в традициях классического искусства и в исполнении известного мастера живописи.</p>\n<p>Художник-портретист А. Арутюнян не противопоставляет искусство фотографии - живописи. Напротив, многие заказы на портреты он выполняет по фотографиям. С одной стороны, это очень удобно заказчику, так как ему не надо тратить время на многочисленные многочасовые сеансы позирования, с другой стороны, по фотоснимку можно сделать заказ на портрет-подарок или сюрприз для знакомых, восстановить облик далекого предка и т.д.</p>\n<p><a href="http://www.serge.dreamarts.com.ua/painting/art_tehnic">Создание портрета по фотографии</a> требует от художника большого мастерства, ведь это не живой человек, а лишь отпечаток его облика. В нем не проявлены черты характера, душевные наклонности, да и некоторые фотоснимки бывают, не похожи на реального человека или они очень старые и плохого качества. Тем не менее, А.Арутюнян в этом искусстве добивается значительных успехов. Его образы, воссозданные по фото, производят сильное впечатление - это настоящие произведения искусства.&nbsp;</p>\n<p>Выполняя множество портретов, самых различных по жанровому своеобразию, он решил выделить изображения, которые удивили бы зрителя своим содержанием, т.к. на них наши современники предстают в неожиданном обличье и интерьере иной исторической эпохи. Так, например, в качестве композиционной основы берется портретный образ кисти известного мастера живописи, но на месте лица исторической личности изображается совсем другой человек: сам заказчик портрета или его знакомый. Видоизменяться в таком портрете может не только лицо, но и другие части тела, поза, образное настроение, стилистика. В результате возникает совершенно неожиданное изображение - сюрприз - вроде бы знакомое, всеми узнаваемое, и в то же время, совсем иное, современное и обновленное. Художник в данной ситуации нередко выступает как советчик и консультант. Именно он может подобрать портретируемому тот образ, в котором он будет смотреться наиболее эффектно. Художник может создать портреты людей, желающих изобразить себя в   военном мундире армии любых стран, в костюмах и образах знатных особ или   литературных героев и т.п.</p>\n<p><strong>Наиболее удачные портреты-сюрпризы</strong>, выполненные Ашотом Арутюняном, размещены на этом сайте.&nbsp; Просмотрев галерею работ художника, вы можете представить себе в каком стиле может быть выполнен Ваш портрет, окунуться в творческую атмосферу и увидеть, как создаются произведения искусства.</p>', 'Портрет сложный и интересный жанр изобразительного искусства. В отличие от фотокарточки портрет является произведением искусства ручной работы, на которое потрачено много времени, эмоциональных и умственных усилий автора, благодаря чему по качеству и глубине передачи он будет намного более сложной и многоуровневой работой, гораздо лучше передающей личность.<br>\r\nПри создании портрета его "истинность" заключается не в том, чтобы произведение напоминало оригинал (в таком случае изображение можно назвать «буквальным»), а в том, чтобы чувства, возбуждаемые произведением, напоминали чувства, вызываемые оригиналом (это уже «эмоциональное» изображение). Когда портрет похож на портретируемого, то зритель, глядя на портрет, чувствует, будто находится в присутствии этого человека. Именно к такому сходству стремится художник. До некоторой степени это получается путём буквального изображения человека, и одновременно этот эффект достигается благодаря искусному отступлению художника от буквального изображения.<br>\r\nПортреты способны зафиксировать человеческую жизнь начиная с младенчества до глубокой старости. Благодаря этому они могут претендовать на историческую достоверность, представляя своего рода зрительно постижимую семейную хронику и являясь носителем фамильного сходства. Портреты всегда украшали залы семейных имений или в соответствии с модой времени специально предназначенные для портретов – "портретные" комнаты. Они были своего рода важными семейными знаками, по портретам можно было проследить генеалогические ветви семьи. <br> \r\nУ портретов была также и эстетическая функция - портретная картина становилась достойным украшением и частью интерьера, художественного быта. И даже в современном интерьере портрет подчеркнет вкус хозяина, его эстетические предпочтения, его идеалы, подчеркнет его статус. Портреты любимых членов семьи создадут теплую и уютную атмосферу в доме, а также они могут украсить и придать солидности офису, кабинету. Ведь настоящая картина масло смотрится куда более богаче и красивее обычной фотографии или постера в любом помещении на любых стенах!'),
(3, 'painting', 'landscapes', '<p>Сегодня человек живет в сумасшедшем темпе, когда почти не остается времени на уединение, на общение с природой, когда блага цивилизации отдаляют нас от первозданной природы. Пейзаж в интерьере комнаты, будь то столовая, или спальня или рабочий кабинет, станет окошком в мир спокойствия и мудрости природы, лирических раздумий, внезапных озарений и вдохновения. Пейзаж может отображать ваше настроение, создавать его. Утром взглянув на свой любимый пейзаж, вы ощущаете прилив энергии и бодрости, который дарит вам изображение природы. Или вечером, после насыщенного рабочего дня глядя на вечные стихии природы вы понимаете, что все жизненные перипетии преходящи, а красота бытия вечна. Ваша любимая картина &ndash; лекарь для вашей души на все случаи жизни.</p>\n<p>Пейзаж, выполненный маслом - это верный способ сделать интерьер любого помещения более живым и изысканным. Иногда бывает чувство, что чего-то не хватает в комнате, где вы находитесь -  стены кажутся голыми, дизайн интерьера, несмотря на всю его продуманность и завершенность, кажется холодным, сухим, безликим. Однако, если подобрать и умело разместить картины, то можно полностью преобразить комнату, сделать ее своеобразной и неповторимой по своему вкусу, близкой и родной. Пейзаж на стене сразу придает уют, а завершает и оттеняет дизайн, и даже довольно тонко отображает вкус хозяина.</p>\n<p>Человек с самых древних времен старался украсить свое жилище, сделать его более уютным и гармоничным. Напртимер, первобытные люди разрисовывали стены пещер, древние греки и римляне вешали на стены яркие коврики, отделывали их росписью и мозаикой. В Греции, а потом и на Руси обязательной составной частью дома была икона. А уже начиная примерно с ХVIII века в интерьере домов стали появляться картины и эстампы в золоченых рамах. Тогда живопись вышла за пределы церковного алтаря и превратилась в предмет роскоши, модную деталь дизайна богатых домов. Оформление интерьера картинами стал все больше входить в моду и остался популярным до сегодняшних дней.</p>\n<p>Если говорить образно, то пейзаж &ndash; это портрет Земли. Действительно хороший пейзаж требует от художника не меньшего мастерства чем, например, портрет. Необходимо  иметь особое дарование, чтобы  не просто срисовать и скопировать изображение, а передать через пейзаж чувства, идею, настроение. Пейзаж это не просто увиденное, а прочувствованное и пропущенное через себя впечатление. Художник слушает природу, чувствует её, отражать в себе и привносить что-то своё. Если вам очень нравятся работы известного автора и хотели бы иметь у себя дома копию его работы, вы можете заказать изготовление копии, которые художник выполнит в технике, максимально соответствующей авторской работе.</p>\n<p>Вы можете<strong> заказать пейзаж в подарок</strong> вашим близким, друзьям, коллегам, который дополнит интерьер их квартиры или офиса, а художник исполнит картину маслом в нужном формате. Воспользовавшись подсказкой, вы можете мужчине подарить морские, городские пейзажи, классические виды природы, охотничьи сюжеты, а также - кабинетные натюрморты со старинными вещами, оружием, письменными принадлежностями. А вот для дамы замечательным подарком станет цветочный натюрморт, романтический морской, колоритный загородный пейзаж или любая другая тема, подчеркивающая ее женскую суть. Для этого заполните форму заказа и отправьте его по электронной почте или позвоните.</p>\n<p>Также вы можете <strong>выбрать для себя что-то из готовых картин</strong> (пейзаж, натюрморт или портрет), исполненный маслом на холсте, для этого вам нужно написать или позвонить и забронировать картину.</p>', 'Сегодня человек живет в сумасшедшем темпе, когда почти не остается времени на уединение, на общение с природой, когда блага цивилизации отдаляют нас от первозданной природы. Пейзаж в интерьере комнаты, будь то столовая, или спальня или рабочий кабинет, станет окошком в мир спокойствия и мудрости природы, лирических раздумий, внезапных озарений и вдохновения. Пейзаж может отображать ваше настроение, создавать его. Утром взглянув на свой любимый пейзаж, вы ощущаете прилив энергии и бодрости, который дарит вам изображение природы. Или вечером, после насыщенного рабочего дня глядя на вечные стихии природы вы понимаете, что все жизненные перипетии преходящи, а красота бытия вечна. Ваша любимая картина – лекарь для вашей души на все случаи жизни.<br>\r\nПейзаж, выполненный маслом - это верный способ сделать интерьер любого помещения более живым и изысканным. Иногда бывает чувство, что чего-то не хватает в комнате, где вы находитесь -  стены кажутся голыми, дизайн интерьера, несмотря на всю его продуманность и завершенность, кажется холодным, сухим, безликим. Однако, если подобрать и умело разместить картины, то можно полностью преобразить комнату, сделать ее своеобразной и неповторимой по своему вкусу, близкой и родной. Пейзаж на стене сразу придает уют, а завершает и оттеняет дизайн, и даже довольно тонко отображает вкус хозяина.<br>\r\nЧеловек с самых древних времен старался украсить свое жилище, сделать его более уютным и гармоничным. Напртимер, первобытные люди разрисовывали стены пещер, древние греки и римляне вешали на стены яркие коврики, отделывали их росписью и мозаикой. В Греции, а потом и на Руси обязательной составной частью дома была икона. А уже начиная примерно с ХVIII века в интерьере домов стали появляться картины и эстампы в золоченых рамах. Тогда живопись вышла за пределы церковного алтаря и превратилась в предмет роскоши, модную деталь дизайна богатых домов. Оформление интерьера картинами стал все больше входить в моду и остался популярным до сегодняшних дней.<br>\r\nЕсли говорить образно, то пейзаж – это портрет Земли. Действительно хороший пейзаж требует от художника не меньшего мастерства чем, например, портрет. Необходимо  иметь особое дарование, чтобы  не просто срисовать и скопировать изображение, а передать через пейзаж чувства, идею, настроение. Пейзаж это не просто увиденное, а прочувствованное и пропущенное через себя впечатление. Художник слушает природу, чувствует её, отражать в себе и привносить что-то своё.\r\nЕсли вам очень нравятся работы известного автора и хотели бы иметь у себя дома копию его работы, вы можете заказать изготовление копии, которые художник выполнит в технике, максимально соответствующей авторской работе.<br>\r\nВы можете заказать пейзаж в подарок вашим близким, друзьям, коллегам, который дополнит интерьер их квартиры или офиса, а художник исполнит картину маслом в нужном формате. Воспользовавшись подсказкой, вы можете мужчине подарить морские, городские пейзажи, классические виды природы, охотничьи сюжеты, а также - кабинетные натюрморты со старинными вещами, оружием, письменными принадлежностями. А вот для дамы замечательным подарком станет цветочный натюрморт, романтический морской, колоритный загородный пейзаж или любая другая тема, подчеркивающая ее женскую суть. Для этого заполните форму заказа и отправьте его по электронной почте или позвоните.<br>\r\nТакже вы можете выбрать для себя что-то из готовых картин (пейзаж, натюрморт или портрет), исполненный маслом на холсте, для этого вам нужно написать или позвонить и забронировать картину. '),
(4, 'painting', 'still_life', '<p>Натюрморт это мир предметов, живущих своей тихой, застывшей, неподвижной жизнью. На картинах этого жанра кроме неодушевлённых предметов художники изображают объекты живой природы, изолированные от своих естественных связей и тем самым обращенные в вещь (рыбу не в реке, а на столе, цветы в букете, а не растущие в поле и т.п). Иногда также композицию дополняют изображения живых, движущихся существ - насекомых, птиц, зверей, даже людей, но лишь как дополнения его основного мотива.</p>\n<p>Натюрморт зародился в XV-XVI веках. Сначала он использовался для украшений фигур Богоматери и Христа, а также его изображали на оборотной стороне алтарного образа. Самыми первыми в этом жанре начали работать фламандские и голландские художники. В основном на натюрмортах изображаются цветы, посуда, фрукты, свечи, вазы. Ещё одной из функций натюрморта была функция утилитарная - натюрмортами разрисовывали шкафы, стены, посуду. С 40-х годов XVII века появился отдельный жанр натюрморта &mdash; цветочный. Цветочные натюрморты были очень популярны. Это объяснялось особенностями быта нидерландского общества, благоприятными природными условиями для развития цветоводства - большинство &laquo;малые голландцы&raquo; имели свои сады, загородные виллы, комнатные растения и хорошо разбирались в цветах.</p>\n<p>Цветы - это самая приятная, распространённая и признанная тема в натюрморте. Цветы привлекают и художников и зрителей своим богатым разноцветьем, фруктово-ягодным буйством и радостной атмосферой летнего праздника, звучанием цвета и тени. Несмотря на то, что слово "натюрморт" (nature morte) буквально переводится с французского как " мёртвая природа", а на английском звучит как "still life" - неподвижная жизнь,  изображенные неодушевленные предметы выглядят очень живо, а иногда и аппетитно.</p>\n<p>Натюрморт это не просто изображение случайно попавших на один стол фруктов, цветов и столовых приборов. Здесь не бывает ни одного лишнего или случайного предмета - каждый цветок, каждый предмет несёт своё символическое значение. В XVII веке издавались специальные эмблематические сборники, в которых объяснялись те или иные символы. Например, драгоценные серебряные сосуды олицетворяли знатность и богатство. Белая лилия свидетельствовала о непорочности. Красная роза - о страстной любви, а белая - о платонической. Тюльпаны свидетельствовали о быстропроходящей, ускользающей красоте. Часто в натюрмортах можно встретить виноградную кисть, символизирующую жертву Спасителя за искупление человеческих грехов. Символом вечной души была порхающая бабочка. Подсолнухи &mdash; это цветы Солнца, относящиеся к стихии огня, сила света, которая защищает от злых сил, приносит удачу в дом. Мак &ndash; это символ небывалой красоты, неувядаемой молодости и женского очарования. Он также - противодействует нечистой силе, защищает от колдунов, ведьм и дурного глаза, а также известен как сильное средство в любовной магии.</p>\n<p>Натюрморт &mdash; уникальный жанр. Жизнь идёт дальше, люди уходят, одни события сменяют другие события, а предметы лежат неподвижно на полотнах. Они вечны, а их реальность застыла. Натюрморт создает ощущение уюта и единения, он не агрессивен, он просто радует глаз, обворожителен своим спокойствием и гармонией. <br /> Удачно подобранный натюрморт завершит ваш стилизованный интерьер, станет его изюминкой, отразит ваш эстетический вкус и настроение, а также станет богатым и символичным украшением комнаты. А изображенные в натюрморте предметы или цветы станут оберегом вашего жилища.</p>\n<p>Вы можете <strong>заказать натюрморт в подарок </strong>вашим близким, друзьям, коллегам, который дополнит интерьер их квартиры или офиса, а художник исполнит картину маслом в нужном формате. Для этого заполните форму заказа и отправьте его по электронной почте или позвоните. Также вы можете <strong>выбрать для себя что-то из готовых картин</strong> (натюрморт или портрет), исполненный маслом на холсте, для этого вам нужно написать или позвонить и забронировать картину.</p>', 'Натюрморт это мир предметов, живущих своей тихой, застывшей, неподвижной жизнью. На картинах этого жанра кроме неодушевлённых предметов художники изображают объекты живой природы, изолированные от своих естественных связей и тем самым обращенные в вещь (рыбу не в реке, а на столе, цветы в букете, а не растущие в поле и т.п). Иногда также композицию дополняют изображения живых, движущихся существ - насекомых, птиц, зверей, даже людей, но лишь как дополнения его основного мотива. <br>\r\nНатюрморт зародился в XV-XVI веках. Сначала он использовался для украшений фигур Богоматери и Христа, а также его изображали на оборотной стороне алтарного образа. Самыми первыми в этом жанре начали работать фламандские и голландские художники. В основном на натюрмортах изображаются цветы, посуда, фрукты, свечи, вазы. Ещё одной из функций натюрморта была функция утилитарная - натюрмортами разрисовывали шкафы, стены, посуду. С 40-х годов XVII века появился отдельный жанр натюрморта — цветочный. Цветочные натюрморты были очень популярны. Это объяснялось особенностями быта нидерландского общества, благоприятными природными условиями для развития цветоводства - большинство «малые голландцы» имели свои сады, загородные виллы, комнатные растения и хорошо разбирались в цветах. <br>\r\nЦветы - это самая приятная, распространённая и признанная тема в натюрморте. Цветы привлекают и художников и зрителей своим богатым разноцветьем, фруктово-ягодным буйством и радостной атмосферой летнего праздника, звучанием цвета и тени. Несмотря на то, что слово "натюрморт" (nature morte) буквально переводится с французского как " мёртвая природа", а на английском звучит как "still life" - неподвижная жизнь,  изображенные неодушевленные предметы выглядят очень живо, а иногда и аппетитно. <br>\r\nНатюрморт это не просто изображение случайно попавших на один стол фруктов, цветов и столовых приборов. Здесь не бывает ни одного лишнего или случайного предмета - каждый цветок, каждый предмет несёт своё символическое значение. В XVII веке издавались специальные эмблематические сборники, в которых объяснялись те или иные символы. Например, драгоценные серебряные сосуды олицетворяли знатность и богатство. Белая лилия свидетельствовала о непорочности. Красная роза - о страстной любви, а белая - о платонической. Тюльпаны свидетельствовали о быстропроходящей, ускользающей красоте. Часто в натюрмортах можно встретить виноградную кисть, символизирующую жертву Спасителя за искупление человеческих грехов. Символом вечной души была порхающая бабочка. Подсолнухи — это цветы Солнца, относящиеся к стихии огня, сила света, которая защищает от злых сил, приносит удачу в дом. Мак – это символ небывалой красоты, неувядаемой молодости и женского очарования. Он также - противодействует нечистой силе, защищает от колдунов, ведьм и дурного глаза, а также известен как сильное средство в любовной магии. <br>\r\nНатюрморт — уникальный жанр. Жизнь идёт дальше, люди уходят, одни события сменяют другие события, а предметы лежат неподвижно на полотнах. Они вечны, а их реальность застыла. Натюрморт создает ощущение уюта и единения, он не агрессивен, он просто радует глаз, обворожителен своим спокойствием и гармонией. <br>\r\nУдачно подобранный натюрморт завершит ваш стилизованный интерьер, станет его изюминкой, отразит ваш эстетический вкус и настроение, а также станет богатым и символичным украшением комнаты. А изображенные в натюрморте предметы или цветы станут оберегом вашего жилища. <br>  \r\nВы можете заказать натюрморт в подарок вашим близким, друзьям, коллегам, который дополнит интерьер их квартиры или офиса, а художник исполнит картину маслом в нужном формате. Для этого заполните форму заказа и отправьте его по электронной почте или позвоните. Также вы можете выбрать для себя что-то из готовых картин (пейзаж, натюрморт или портрет), исполненный маслом на холсте, для этого вам нужно написать или позвонить и забронировать картину. ');
INSERT INTO `category` (`id`, `cat_name`, `part_name`, `text_short`, `text_long`) VALUES
(5, 'placards', '', 'В нашей жизни стремительных скоростей и быстрых перемен каждая компания желает быть первой, и в этом помогает грамотно построенная рекламная политика своего продукта и, конечно же – "Его Величество" рекламный плакат! Сегодня рекламный плакат это один из самых распространенных и эффективных видов наружной рекламы. Он с нами повсюду – на улицах, в метро, в маршрутных такси и автобусах, в магазинах, в кинотеатрах и развлекательных центрах, и даже в подъездах их расклеивают с надеждой обратить внимание потребителей на товар или услугу. Рекламный плакат – великий двигатель торговли - мимо него ежедневно проходят тысячи людей. Из 10 потенциальных потребителей каждый 8 взглянет на плакат и отметит для себя, нужен ли ему этот товар. Иногда рекламный плакат отлично "работает", привлекая внимание и побуждая купить, а иногда просто раздражает или оставляет безразличными потенциальных покупателей. Поэтому на дизайнерское исполнение, содержание и качество печати рекламного плаката делается главная ставка.<br>\r\nСоздать рекламный плакат на первый взгляд может показаться очень легко – придумай что-то необычное, яркое, броское, красивую девушку (желательно поменьше одетую) туда же, добавить пару надписей и плакат готов. И в результате получаем что-то действительно ярко-скандальное, агрессивно-креативное, но малопонятного содержания - что купить, чем воспользоваться, куда пойти предлагают, непонятно. Например, на рекламном плакате изображена привлекательная слегка одетая с ярким макияжем девушка в строительной робе и каске, а в руке дрель, попробуйте, догадайтесь, что это - реклама строительной компании или торговой фирмы, которая предлагает спецодежду или инструменты? <br>\r\nРекламный плакат с грамотно составленным дизайном  это четкое визуальное сообщение, формирующее отношение к товару, демонстрирующее его с лучшей стороны, побуждающее потенциального потребителя к выводу и конкретным действиям – выбору или покупке. <br>\r\nИстория рекламного плаката как жанра начинается со 2-й половины XIX века, когда в Западной Европе на шрифтовых театральных афишах и объявлениях все большее место стали занимать орнаменты и фигурные изображения. В результате такой эволюции возник рекламный плакат. Вообще первый рекламный плакат был создан вручную примерно так: неизвестный торговец, имя которого затерялось в истории, придумал написать на листе бумаги адрес своей лавки и информацию о своем товаре стал расклеивать такие листовки на столбах и стенах. Популярность плакатов росла с развитием общественно-политической и культурной жизни - увеличением количества зрелищных учреждений, промышленных и художественных выставок, с появлением митингов и манифестаций.  С того времени после изготовления плакатов рукописных их изготавливали методом литографии, а современный плакат обычно является произведением полиграфического искусства. С развитием печатного дела получил развитие и рекламный бизнес! Плакаты стали более приятными на вид, они передавали истинный вид предлагаемого продавцами товара.<br>\r\nСвои первые рекламные плакаты автор начал создавать в 1980-е годы, когда понятие рекламы только начинало входить в постсоветское пространство. В своих рекламных плакатах автор использовал художественные метафоры, фигуры разного масштаба, изображение событий происходящих в разное время и в разных местах, контурное обозначение предметов, использовал фотографии в сочетании с рисунком и с живописью, и многие его работы отличаются информационной образностью  и экспрессивностью. <br> \r\nХудожник открыл в 1987 году первое в Украине и второе в Союзе частное рекламное агентство. "Целый год пришлось ходить в исполком: рекламное агентство не хотели регистрировать," - вспоминает Ашот. – "Пришлось применить хитрость и прийти прямо к председателю исполкома с телекамерой и журналистом. Чиновник начал психовать, орать и чуть камеру не разбил. Возмущался: пока я буду здесь, у меня в районе не будет никаких пепсикол и никаких рекламных кооперативов!" Но все же предприятие зарегистрировали. Ашот поставил первую рекламу «Пепси-колы» на троллейбусах, на трамваях, такси, на мостах, на ЦУМе, а также первый биллборд в Киеве, который появился возле кинотеатра «Дружба» на Крещатике в 1989 году.  ', 'В нашей жизни стремительных скоростей и быстрых перемен каждая компания желает быть первой, и в этом помогает грамотно построенная рекламная политика своего продукта и, конечно же – "Его Величество" рекламный плакат! Сегодня рекламный плакат это один из самых распространенных и эффективных видов наружной рекламы. Он с нами повсюду – на улицах, в метро, в маршрутных такси и автобусах, в магазинах, в кинотеатрах и развлекательных центрах, и даже в подъездах их расклеивают с надеждой обратить внимание потребителей на товар или услугу. Рекламный плакат – великий двигатель торговли - мимо него ежедневно проходят тысячи людей. Из 10 потенциальных потребителей каждый 8 взглянет на плакат и отметит для себя, нужен ли ему этот товар. Иногда рекламный плакат отлично "работает", привлекая внимание и побуждая купить, а иногда просто раздражает или оставляет безразличными потенциальных покупателей. Поэтому на дизайнерское исполнение, содержание и качество печати рекламного плаката делается главная ставка.<br>\r\nСоздать рекламный плакат на первый взгляд может показаться очень легко – придумай что-то необычное, яркое, броское, красивую девушку (желательно поменьше одетую) туда же, добавить пару надписей и плакат готов. И в результате получаем что-то действительно ярко-скандальное, агрессивно-креативное, но малопонятного содержания - что купить, чем воспользоваться, куда пойти предлагают, непонятно. Например, на рекламном плакате изображена привлекательная слегка одетая с ярким макияжем девушка в строительной робе и каске, а в руке дрель, попробуйте, догадайтесь, что это - реклама строительной компании или торговой фирмы, которая предлагает спецодежду или инструменты? <br>\r\nРекламный плакат с грамотно составленным дизайном  это четкое визуальное сообщение, формирующее отношение к товару, демонстрирующее его с лучшей стороны, побуждающее потенциального потребителя к выводу и конкретным действиям – выбору или покупке. <br>\r\nИстория рекламного плаката как жанра начинается со 2-й половины XIX века, когда в Западной Европе на шрифтовых театральных афишах и объявлениях все большее место стали занимать орнаменты и фигурные изображения. В результате такой эволюции возник рекламный плакат. Вообще первый рекламный плакат был создан вручную примерно так: неизвестный торговец, имя которого затерялось в истории, придумал написать на листе бумаги адрес своей лавки и информацию о своем товаре стал расклеивать такие листовки на столбах и стенах. Популярность плакатов росла с развитием общественно-политической и культурной жизни - увеличением количества зрелищных учреждений, промышленных и художественных выставок, с появлением митингов и манифестаций.  С того времени после изготовления плакатов рукописных их изготавливали методом литографии, а современный плакат обычно является произведением полиграфического искусства. С развитием печатного дела получил развитие и рекламный бизнес! Плакаты стали более приятными на вид, они передавали истинный вид предлагаемого продавцами товара.<br>\r\nСвои первые рекламные плакаты автор начал создавать в 1980-е годы, когда понятие рекламы только начинало входить в постсоветское пространство. В своих рекламных плакатах автор использовал художественные метафоры, фигуры разного масштаба, изображение событий происходящих в разное время и в разных местах, контурное обозначение предметов, использовал фотографии в сочетании с рисунком и с живописью, и многие его работы отличаются информационной образностью  и экспрессивностью. <br> \r\nХудожник открыл в 1987 году первое в Украине и второе в Союзе частное рекламное агентство. "Целый год пришлось ходить в исполком: рекламное агентство не хотели регистрировать," - вспоминает Ашот. – "Пришлось применить хитрость и прийти прямо к председателю исполкома с телекамерой и журналистом. Чиновник начал психовать, орать и чуть камеру не разбил. Возмущался: пока я буду здесь, у меня в районе не будет никаких пепсикол и никаких рекламных кооперативов!" Но все же предприятие зарегистрировали. Ашот поставил первую рекламу «Пепси-колы» на троллейбусах, на трамваях, такси, на мостах, на ЦУМе, а также первый биллборд в Киеве, который появился возле кинотеатра «Дружба» на Крещатике в 1989 году.  '),
(6, 'photos', 'ukraine', '<p style="text-align: right; padding-left: 30px;"><em><img style="float: left; margin-left: 10px; margin-right: 10px;" alt="Ukraine" src="/img/upload_tiny/ukraina (1).jpg" width="250" height="159" />Любіть Україну, як сонце, любіть, як вітер, і трави, і води,</em><br /><em>в годину щасливу і в радості мить, любіть у годину негоди!</em><br /><em>Любіть Україну у сні й наяву, вишневу свою Україну,</em><br /><em>красу її, вічно живу і нову, і мову її солов''їну.</em><br /><em>Для нас вона в світі єдина, одна в просторів солодкому чарі...</em><br /><em>Вона у зірках, і у вербах вона, і в кожному серця ударі...<br />Любіть у коханні, в труді, у бою, як пісню, що лине зорею...</em><br /><em>Всім серцем любіть Україну свою - і вічні ми будемо з нею! </em><br /><em>Володимир Сосюра</em></p>', 'Ориентируясь на эти показатели, руководители, менеджеры могут корректировать как свои управленческие действия, так и процесс деятельности работников. \r\nАмериканские ученые-бихевиористы [3] выделяют три «психических состояния», испытываемые работником, которые определяют его удовлетворенность работой и профессиональную мотивацию: ощущение значимости (работник должен чувствовать, что его деятельность является стоящей и важной в принятой им системе ценностей); ощущение ответственности (он должен быть уверен, что несет личную ответственность за результат своих действий); знание результата (он должен иметь возможность оценить результаты своей работы). \r\nВ соответствии с этими взглядами Т. Соломанидина и В. Соломанидин [1] приводят принципы проектирования работы с точки зрения удовлетворенности ее исполнителей. В такой перечень они включили следующие требования\r\n'),
(7, 'cinemaplacard', '', '<p>Кинематограф - искусство зримое, поэтому предварительное оповещение зрителей не может обойтись без визуальной презентации премьеры. Одним из таких инструментов визуального промоушена фильмов является киноплакат или киноафиша. Цена билета на любой кинофильм приблизительно одинакова, поэтому по его стоимости нельзя прогнозировать ценность киновпечатления. Зрителю важны любые источники информации, которые смогли бы его хоть как-то сориентировать до просмотра - он ищет знакомые фамилии актеров и режиссеров, пытается прочитать на форумах рецензии, впечатления, по которым можно открыть непредсказуемость непросмотренного кино.</p>\n<p>Главным средством продвижения кинофильмов на рынок в XX веке являлись киноплакаты или киноафиши. Киноафиша, исполненная художником перед премьерой фильма, была единственным входом в пространство картины, первые опознавательные сигналы стиля, жанра и качества предстоящего удовольствия. Еще до момента попадания в зал зритель пытаясь распознать содержание фильма, зритель знакомился с афишей - культурным феноменом, порожденным кинематографом, зазывными и опознавательными знаками в лабиринтах кинокартин. Первоначально киноплакаты создавались на основе отображения отдельных кадров фильма. Позже в плакат добавилась образность, стремление показать главных героев, передать жанр фильма, раскрыть содержание фильма. Вместе с тем киноафиша всегда несла важнейшие черты творческой индивидуальности своего создателя.</p>\n<p>В наши дни в век компьютерных технологий и достижений полиграфии возле кинотеатров красуются сверстанные на компьютерах и напечатанные в типографии афиши. А еще в 70-80-х годах прошлого столетия афиши от руки рисовали - огромный сложнейший киноплакат делался за ночь. Ашот работал художником в кинотеатрах &laquo;Киев&raquo; и &laquo;Кинопанорама&raquo;, и весь Киев любовался его киноафишами. О его афишах писали городские газеты, снимали программы, люди шли толпами на разрекламированные фильмы. Жизнь киноплаката чрезвычайно коротка и ограничена периодом экранного показа, а афиши Ашота сами редко доживали свой век &ndash; их просто воровали. За некоторые слишком откровенные, по тем меркам, рисунки автора даже хотели уволить. Тогда, например, изображение обнаженных женских ног было под запретом. Была история с одной афишей, где художник нарисовал очень реалистично пачку пятирублевок, и его вызывали в КГБ и милицию как вероятного кандидата в фальшивомонетчики.</p>\n<p>Некоторые свои работы Ашот успел сфотографировать. Афиши эти действительно уникальны &ndash; сложно поверить, что все эти портреты актеров со сложнейшими сюжетами и кое-где сюрреалистическими элементами нарисованы от руки, да еще и за ночь. Художник стал настолько знаменит своими афишами, что его даже хотели сделать директором кинотеатра, и отправили учиться в театральный институт на киноведческий факультет. Но начались времена перестройки и с приходом в быт видеомагнитофонов кинотеатры начали приходить в упадок. Художник не верил в их возрождение, и афиши остались в его творчестве как визуальная летописью репертуаров кинотеатров и как еще один этап творческого роста и развития.</p>', 'Кинематограф - искусство зримое, поэтому предварительное оповещение зрителей не может обойтись без визуальной презентации премьеры. Одним из таких инструментов визуального промоушена фильмов является киноплакат или киноафиша. Цена билета на любой кинофильм приблизительно одинакова, поэтому по его стоимости нельзя прогнозировать ценность киновпечатления. Зрителю важны любые источники информации, которые смогли бы его хоть как-то сориентировать до просмотра - он ищет знакомые фамилии актеров и режиссеров, пытается прочитать на форумах рецензии, впечатления, по которым можно открыть непредсказуемость непросмотренного кино. <br>\r\nГлавным средством продвижения кинофильмов на рынок в XX веке являлись киноплакаты или киноафиши. Киноафиша, исполненная художником перед премьерой фильма, была единственным входом в пространство картины, первые опознавательные сигналы стиля, жанра и качества предстоящего удовольствия. Еще до момента попадания в зал зритель пытаясь распознать содержание фильма, зритель знакомился с афишей - культурным феноменом, порожденным кинематографом, зазывными и опознавательными знаками в лабиринтах кинокартин. Первоначально киноплакаты создавались на основе отображения отдельных кадров фильма. Позже в плакат добавилась образность, стремление показать главных героев, передать жанр фильма, раскрыть содержание фильма. Вместе с тем киноафиша всегда несла важнейшие черты творческой индивидуальности своего создателя. <br>\r\nВ наши дни в век компьютерных технологий и достижений полиграфии возле кинотеатров красуются сверстанные на компьютерах и напечатанные в типографии афиши. А еще в 70-80-х годах прошлого столетия афиши от руки рисовали - огромный сложнейший киноплакат делался за ночь. Ашот работал художником в кинотеатрах «Киев», и «Кинопанорама» и весь Киев любовался его киноафишами. О его афишах писали городские газеты, снимали программы, люди шли толпами на разрекламированные фильмы. Жизнь киноплаката чрезвычайно коротка и ограничена периодом экранного показа, а афиши Ашота сами редко доживали свой век – их просто воровали. За некоторые слишком откровенные, по тем меркам, рисунки автора даже хотели уволить. Тогда, например, изображение обнаженных женских ног было под запретом. Была история с одной афишей, где художник нарисовал очень реалистично пачку пятирублевок, и его вызывали в КГБ и милицию как вероятного кандидата в фальшивомонетчики. <br>\r\nНекоторые свои работы Ашот успел сфотографировать. Афиши эти действительно уникальны – сложно поверить, что все эти портреты актеров со сложнейшими сюжетами и кое-где сюрреалистическими элементами нарисованы от руки, да еще и за ночь. Художник стал настолько знаменит своими афишами, что его даже хотели сделать директором кинотеатра, и отправили учиться в театральный институт на киноведческий факультет. Но начались времена перестройки и с приходом в быт видеомагнитофонов кинотеатры начали приходить в упадок. Художник не верил в их возрождение, и афиши остались в его творчестве как визуальная летописью репертуаров кинотеатров и как еще один этап творческого роста и развития.'),
(8, 'photos', 'kids', '<p style="text-align: right; padding-left: 30px;"><em><img style="float: left;" alt="child" src="/img/upload_tiny/poetry_newborn.jpg" width="230" height="173" />Малютка, милое дитя! Тебя приветствую душою.</em><br /><em>Взгляни скорей на божий свет: он дышит прелестью святою.</em><br /><em>В нем, с резвой радостью, краса живет всегда, не разлучаясь...</em><br /><em>Ты счастлив будешь, ангел мой, прекрасной жизнью наслаждаясь...</em><br /><em>Да, я завидую тебе...</em><em> Ты только цвесть лишь начинаешь &ndash; <br />И, на тебя порой смотря,</em><em> невольно детство вспоминаешь...</em><br /><em>Спеши ж прекрасное дитя, младыми днями насладиться:</em><br /><em>Ведь наша юность &ndash; сладкий сон, который больше не приснится!</em><br /><em>Леонид Глибов</em></p>', 'Весьма существенной составляющей является удовлетворенность окружением . Зачастую оно настолько сильно влияет на настроение, трудоспособность, желание продолжать работу на этом месте, что при определенных негативных обстоятельствах человек может принять решение об увольнении. В силу этого в последнее время востребованы тренинги, целью которых является формирование командных отношений в коллективе. В то же время для удовлетворенности работой вполне достаточно, чтобы окружение не вызывало негативных эмоций, например раздражения. Психологи также рекомендуют избегать создания каких-то специальных привилегий для руководства, которые увеличивают разрыв между ним и остальными работниками. '),
(9, 'restaurant', '', '<p><img style="float: left; margin-left: 10px; margin-right: 10px;" alt="Napoleon" src="/img/upload_tiny/1001.jpg" height="236" width="200" />Всем известно, что театр начинается с вешалки. А ресторан? Полагаете со швейцара? Но здесь, увидев у входа настоящих гвардейцев Наполеона, блистающих великолепной выправкой и эполетами, вы поймете, что и в наше прозаическое время есть место чудесам. Мы на пороге сказочного мира, созданного фантазией художника Ашота Арутюняна - основателя и владельца ресторана НАПОЛЕОН.</p>\r\n<p><strong>Ресторан "Наполеон" </strong>был открыт в Киеве в 1993 году на ул. Шота Руставели, и пользовался большой популярностью у зарождающейся украинской буржуазии и культурной богемы. Ашот после визита в Америку, где его впечатлил ресторан одного рекламного агентства, решил сделать свой. Шикарный, как любимый император, этот ресторан впечатлял своим бархатно-мраморным великолепием. Удивительно воссоздана атмосфера великой эпохи. Путешествие во времени оказалось возможным. И вот вы во дворце Наполеона. Имперская роскошь интерьеров, алые камзолы гвардейцев. Перед входом в ресторан - искусственные деревья. Они были в 1993 году единственные в Киеве.  Народ,  проходя мимо, трогал и не верил, что зимой, в 20-ти градусный  мороз деревья  могут быть зелеными... Советский человек такого еще не  видел!</p>\r\n<p>К сожалению жизнь ресторана была недолгой - когда был период бандитского передела,&nbsp; &laquo;Наполеон&raquo; у Ашота, по его словам, забрали. И никто ничего не мог сделать &ndash; ни власть, ни милиция. А чтобы его труды не достались врагу, все содержимое ресторана хозяин раздал сотрудникам &ndash; кто что мог унести, а гранит и мрамор ушел на надгробные памятники. Себе Ашот оставил мини-музей Наполеона, в его доме нашли место медали, фарфор, сабли и огромный портрет императора, который он сам писал для ресторана. И теперь вы можете только виртуально посетить ресторан "Наполеон", просмотрев фотографии залов, увидеть шикарные камзолы персонала и императорские блюда, которые подавали в ресторане.</p>\r\n<table border="0">\r\n<tbody>\r\n<tr>\r\n<td style="text-align: left;"><img style="display: block; float: left; margin-left: 10px; margin-right: 10px;" alt="zal_napoleon" src="/img/upload_tiny/napH.jpg" height="130" width="100" /><strong>Зал Наполеон </strong>- жемчужина ресторана. Столик на четверых.   Сочетание пурпурных и белых тонов, роскошь, величие, неизменные   гвардейцы. Этот зал достоин императора. Здесь мог бы произойти прием   послов или военный совет. Вот уже собираются приближенные. Еще минута - и   мы услышим быстрые шаги великого корсиканца! Это ли не поступь   империи?!</td>\r\n<td style="text-align: left;"><img style="float: left; margin-left: 10px; margin-right: 10px;" alt="Jozephina" src="/img/upload_tiny/rosy.jpg" width="100" /><strong>Розовый зал Жозефина</strong> самый изящный<strong> </strong>со столиком на  шесть человек. Интимная камерная атмосфера, создаваемая этим интерьером,  сделает вечер незабываемым. Розовые перламутровые блики будут играть на  лицах ваших спутниц. Может быть, каждая из них почувствует себя  придворной дамой или даже самой... Вам нравятся царственные женщины?</td>\r\n<td style="text-align: left;"><strong><img style="display: block; float: left; margin-left: 10px; margin-right: 10px;" alt="zal_white" src="/img/upload_tiny/white.jpg" height="130" width="100" /></strong><strong>Белый зал</strong> рассчитан на 10-15 человек. Ужиная здесь, вы   будете пытаться соответствовать этим сверкающим стенам и поистине   дворцовому обслуживанию. Вечер окажется изысканным и церемонным.   Подумайте, какое событие вы хотели бы отметить в этом зале. Юбилей?   Встречу с друзьями? Успех? А может быть, свадьбу?</td>\r\n</tr>\r\n</tbody>\r\n</table>', 'Всем известно, что театр начинается с вешалки. А ресторан? Полагаете со швейцара? Но здесь, увидев у входа настоящих гвардейцев Наполеона, блистающих великолепной выправкой и эполетами, вы поймете, что и в наше прозаическое время есть место чудесам. Мы на пороге сказочного мира, созданного фантазией художника Ашота Арутюняна - основателя и владельца ресторана НАПОЛЕОН.<br>\r\nРесторан был открыт в Киеве в 1993 году на ул. Шота Руставели, и пользовался большой популярностью у зарождающейся украинской буржуазии и культурной богемы.\r\nУдивительно воссоздана атмосфера великой эпохи. Путешествие во времени оказалось возможным. И вот вы во дворце Наполеона. Имперская роскошь интерьеров, алые камзолы гвардейцев. '),
(10, 'photos', 'people', '<p style="text-align: right; padding-left: 30px;"><em><img style="float: left;" alt="man" src="/img/upload_tiny/nature (8).jpg" width="220" height="177" /></em><em>Ти знаєш, що ти &mdash; людина? Ти знаєш про це чи ні?</em><br /><em>Усмішка твоя &mdash; єдина, мука твоя &mdash; єдина, очі твої &mdash; одні.</em><br /><em>Більше тебе не буде завтра на цій землі</em><br /><em>Інші ходитимуть люди, інші кохатимуть люди &mdash; добрі, ласкаві й злі.</em><br /><em>Сьогодні усе для тебе &mdash; озера, гаї, степи.</em><br /><em>І жити спішити треба, кохати спішити треба &mdash; гляди ж не проспи!</em><br /><em>Бо ти на землі &mdash; людина, і хочеш того чи ні &mdash;</em><br /><em>Усмішка твоя &mdash; єдина, мука твоя &mdash; єдина, очі твої &mdash; одні.</em><br /><em>Василь Симоненко</em></p>', 'В целом же на индивидуальном уровне удовлетворенность работой каждого связана с удовлетворением уникального сочетания его актуальных потребностей. Достижение этой цели выдвигает в качестве одной из основных задач кадрового менеджмента изучение потребностей претендентов на вакантные должности и работников предприятия. Одним из практичных и достаточно эффективных инструментов, которые можно использовать с этой целью, является методика изучения мотивационного профиля личности, которую разработали американские ученые Шейла Ричи и Питер Мартин [1]. Эти ученые выделили двенадцать основных потребностей человека и предложили тест для изучения их относительной значимости (актуальности) для этого работника. Количественно выраженную значимость этих потребностей авторы методики называют мотивационным профилем. '),
(11, 'photos', 'kiev', '<p style="text-align: right; padding-left: 30px;"><em><img style="float: left;" alt="Kiev" src="/img/upload_tiny/big_700.JPG" width="230" height="194" />Грає море зелене, тихий день догора.</em><em> <br />Дорогими для мене стали схили Дніпра,</em><br /><em>Де колишуться віти закоханих мрій...</em><em> <br />Як тебе не любити, Києве мій!</em><br /><em></em><br /><em>Спить натомлене місто мирним, лагідним сном,</em><em> <br />Ген вогні, як намисто, розцвіли над Дніпром,</em><br /><em>Вечорів оксамити, мов щастя прибій...</em><em> <br />Як тебе не любити, Києве мій!</em><br /><em></em></p>\r\n<p style="text-align: right; padding-left: 60px;"><em>Дмитро Луценко</em></p>', 'Рассмотрим пример. В кадровую службу предприятия передана информация о том, что одна из работниц, весьма успешно справлявшаяся со своими обязанностями, высказывает намерения об уходе с данной работы. При изучении ее мотивационного профиля как самая актуальная была выявлена потребность в признании. Руководство практически не замечало ее хорошей работы. Кадровой службой были даны рекомендации руководителю относительно необходимости признания хорошо выполненной работы, высказывания устной благодарности. Этого оказалось достаточно, чтобы повысить у работницы удовлетворенность работой и удержать ее в организации. При этом даже не понадобилось дополнительных финансовых затрат. '),
(14, 'photos', 'medicine', '<p style="text-align: right;"><em><img style="float: left; margin-left: 10px; margin-right: 10px;" alt="medicina" src="/img/upload_tiny/medicina_1.jpg" width="250" height="167" />Клянусь ... исполнять честно,  соответственно моим силам и моему разумению, следующую присягу и  письменное обязательство...</em><em> Я направляю режим больных к их выгоде сообразно с моими силами и моим  разумением, воздерживаясь от причинения всякого вреда и  несправедливости. Я не дам никому просимого у меня смертельного средства  и не покажу пути для подобного замысла. Чисто и непорочно буду я проводить свою  жизнь и свое искусство. В какой бы дом я ни вошел, я войду туда для пользы больного...</em><em> Чтобы я ни увидел или ни услышал  касательно жизни людской из того, что не следует когда-либо разглашать, я  умолчу о том, считая подобные вещи тайной. Мне, нерушимо выполняющему  клятву, да будет дано счастье в жизни и в искусстве и слава у всех людей  на вечные времена...</em></p>\r\n<p style="text-align: right;"><em>Клятва Гиппократа (сокращенно, перевод на русский язык)<br /></em></p>', 'С целью повышения разнообразия используемых умений и навыков возможно совмещение определенных функций или дополнение теми, которые ранее работником не выполнялись. Повышение целостности работы может быть достигнуто добавлением заключительных операций, в процессе выполнения которых работник будет иметь дело с видимым результатом деятельности. Однако следует помнить, что добавление рабочих операций более низкого уровня, которые не делают работу более ценной, обычно вызывает у работника чувство неудовлетворенности и снижает уровень мотивации. Увеличение важности работы связано с информированием работника о том, как конкретно будут использованы результаты его труда. ');
INSERT INTO `category` (`id`, `cat_name`, `part_name`, `text_short`, `text_long`) VALUES
(15, 'about', '', '<p><img style="float: left; margin: 10px;" src="/img/upload_tiny/photo_ashot.png" width="150" height="232" /><strong>Ашот Арутюнян - народный художник Армении, заслуженный деятель искусств</strong><br /> Закончил художественную школу в г. Ереване, Криворожское авиаучилище спецслужб и Киевский театральный институт имени Карпенко-Карого. Живет и работает в Киеве с 1977 года. Долгие годы работал в кинотеатрах &laquo;Кинопанорама&raquo; и &laquo;Киев&raquo; художником, а также рисовал <a target="_blank" href="/cinemaplacard">афиши</a> для объединения &laquo;Кинопрокат&raquo;. Создавал киноплакаты для В/О &laquo;Совэкспортфильм&raquo;, политические плакаты. Участвовал в различных международных конкурсах плаката. Весь Киев любовался киноафишами Ашота. О его афишах писали городские газеты, снимали программы, люди шли толпами на разрекламированные фильмы. Афиши эти действительно уникальны &ndash; сложно поверить, что все эти портреты актеров со сложнейшими сюжетами и кое-где сюрреалистическими элементами нарисованы от руки, да еще и за ночь. Афиши Ашота сами редко доживали свой век &ndash; их просто воровали. Некоторые свои работы Ашот успел сфотографировать. Художник стал настолько знаменит своими афишами, что ему даже предлагали стать директором кинотеатра.<br /> В 1987 году открыл кооперативное рекламное агентство &laquo;Ракурс&raquo; - первое в Украине и второе в Союзе частное рекламное агентство. Ашот поставил первую <a target="_blank" href="/placards">рекламу</a> &laquo;Пепси-колы&raquo; на троллейбусах, на трамваях, такси, на мостах, на ЦУМе, а также первый биллборд в Киеве, который появился возле кинотеатра &laquo;Дружба&raquo; на Крещатике в 1989 году.<br /> В 1993 году Ашот после визита в Америку, где его впечатлил ресторан одного рекламного агентства, решил сделать свой - ресторан премиум-класса &laquo;Наполеон&raquo;. <a target="_blank" href="/restaurant">Ресторан "Наполеон"</a> был открыт в Киеве на ул. Шота Руставели, и пользовался большой популярностью у зарождающейся украинской буржуазии и культурной богемы. Шикарный, как любимый император, этот ресторан впечатлял своим бархатно-мраморным великолепием, в нем удивительно точно была воссоздана атмосфера великой эпохи. Путешествие во времени оказалось возможным - имперская роскошь интерьеров, алые камзолы гвардейцев&hellip; К сожалению жизнь ресторана была недолгой и завершилась в период бандитского передела. Себе Ашот оставил мини-музей Наполеона, в его доме нашли место медали, фарфор, сабли и огромный портрет императора, который он сам писал для ресторана.<br /> С 1997 года занялся издательским делом, когда в Украине почти не печатали новых книг. Издал много авторских <a target="_blank" href="/books">фотоальбомов</a>, в том числе заказных, среди которых:</p>\r\n<ul>\r\n<li> <strong>шикарные красочные издания, где воссоздан образ живописной природы и архитектуры стран</strong>: &laquo;Мальовнична Україна&raquo; (1997), &laquo;Ливия&raquo; (1999),</li>\r\n<li> <strong>альбомы, которые заказали с высот Печерских холмов</strong>: &laquo;Леонід Кучма. Президент України&raquo; (1998), &laquo;Леонід Кучма&raquo; (2003), &laquo;Леонід Каденюк: &laquo;Я твій син, Україно!&raquo; (1998), &laquo;Виктор Черномырдин. Пять лет в Украине&raquo; (2006),</li>\r\n<li> <strong>серия книг о Киеве</strong>: &laquo;Київ&raquo; (2000), &laquo;Київ вчора і сьогодні&raquo; (2002), &laquo;Киев на старой почтовой открытке&raquo; (2005), &laquo;Старий Київ&raquo; (2005), &laquo;Київський Національний університет імені Тараса Шевченка&raquo; (2004),</li>\r\n<li> <strong>исторические фотоальбомы</strong>: &laquo;Так, Україна!&raquo; (2005), &laquo;Вікопомний подвиг&raquo; (2006), &laquo;Страна, которой нет&hellip;&raquo; (2007).</li>\r\n</ul>\r\n<p>Больше всего Ашот увлечен историей Киева &ndash; в своих фотоальбомах он показывает город, с которым связала его судьба, в котором происходило его формирование как творческой личности. В фотоальбомах и книгах художника собраны уникальные архивные фотоматериалы. Они интересны тем, кто увлекается историей и архитектурой, и тем, кому интересно увидеть старый Киев, который мы уже безвозвратно потеряли.<br /> Сейчас художник занимается живописью, создает картины маслом <a target="_blank" href="/painting/portrets">портреты</a>, <a target="_blank" href="/painting/still_life">натюрморты</a>. В своих работах с фотографической точностью он передает черты лица, тонкости интерьера, природы или предметов. Ашот хочет, чтобы искусство было ближе к людям, предлагает создание портретов в качестве подарков к знаменательным событиям или как начало фамильной портретной галереи. Картины всегда были и остаются достойным украшением и частью интерьера, художественного быта. И даже в современном интерьере живопись подчеркнет вкус хозяина, его эстетические предпочтения, его идеалы, статус. Портреты любимых членов семьи создадут теплую и уютную атмосферу в доме, а также они могут украсить и придать солидности офису или кабинету. Художник также планирует создать частную галерею.</p>', '<img style="float: left; margin: 10px;" src="/img/upload_tiny/photo_ashot.png" width="150" height="232" />Ашот Арутюнян – закончил художественную школу в г. Ереване, Криворожское авиаучилище спецслужб и Киевский театральный институт имени Карпенко-Карого. Живет и работает в Киеве с 1977 года. Долгие годы работал в кинотеатрах «Кинопанорама» и «Киев» художником, а также рисовал афишы для объединения «Кинопрокат». Создавал киноплакаты для В/О «Совэкспортфильм», политические плакаты. Участвовал в различных международных конкурсах плаката. Весь Киев любовался киноафишами Ашота. О его афишах писали городские газеты, снимали программы, люди шли толпами на разрекламированные фильмы. Афиши эти действительно уникальны – сложно поверить, что все эти портреты актеров со сложнейшими сюжетами и кое-где сюрреалистическими элементами нарисованы от руки, да еще и за ночь. Афиши Ашота сами редко доживали свой век – их просто воровали. Некоторые свои работы Ашот успел сфотографировать. Художник стал настолько знаменит своими афишами, что ему даже предлагали стать директором кинотеатра.<br>\r\nВ 1987 году открыл кооперативное рекламное агентство «Ракурс» - первое в Украине и второе в Союзе частное рекламное агентство. Ашот поставил первую рекламу «Пепси-колы» на троллейбусах, на трамваях, такси, на мостах, на ЦУМе, а также первый биллборд в Киеве, который появился возле кинотеатра «Дружба» на Крещатике в 1989 году.<br>  \r\nВ 1993 году Ашот после визита в Америку, где его впечатлил ресторан одного рекламного агентства, решил сделать свой - ресторан премиум-класса «Наполеон». Ресторан "Наполеон" был открыт в Киеве на ул. Шота Руставели, и пользовался большой популярностью у зарождающейся украинской буржуазии и культурной богемы. Шикарный, как любимый император, этот ресторан впечатлял своим бархатно-мраморным великолепием, в нем удивительно точно была воссоздана атмосфера великой эпохи. Путешествие во времени оказалось возможным - имперская роскошь интерьеров, алые камзолы гвардейцев… К сожалению жизнь ресторана была недолгой и завершилась в период бандитского передела. Себе Ашот оставил мини-музей Наполеона, в его доме нашли место медали, фарфор, сабли и огромный портрет императора, который он сам писал для ресторана.<br> \r\nС 1997 года занялся издательским делом, когда в Украине почти не печатали новых книг. Издал много авторских фотоальбомов, в том числе заказных, среди которых: \r\n«Мальовнична Україна» - 1997, <br>\r\n«Леонід Кучма. Президент України» - 1998, «Леонід Кучма» - 2003, <br>\r\n«Леонід Каденюк: «Я твій син, Україно!» - 1998, <br>\r\n«Ливия» - 1999, <br>\r\n«Київський Національний університет імені Тараса Шевченка» - 2004, <br>\r\n«Київ» - 2000, «Київ вчора і сьогодні» - 2002, «Киев на старой почтовой открытке» - 2005, «Старий Київ» - 2005, «Вікопомний подвиг» - 2006, <br>\r\n«Так, Україна!» - 2005, <br>\r\n«Виктор Черномырдин. Пять лет в Украине» - 2006, <br>\r\n«Страна, которой нет…» - 2007.<br> \r\nБольше всего Ашот увлечен историей Киева – в своих фотоальбомах он показывает город, с которым связала его судьба, в котором происходило его формирование как творческой личности. В фотоальбомах и книгах художника собраны уникальные архивные фотоматериалы. Они интересны тем, кто увлекается историей и архитектурой, и тем, кому интересно увидеть старый Киев, который мы уже безвозвратно потеряли.<br>\r\nСейчас художник занимается живописью – создает картины маслом – портреты, пейзажи, натюрморты. В своих работах с фотографической точностью он передает черты лица, тонкости интерьера, природы или предметов. Ашот хочет, чтобы искусство было ближе к людям, предлагает создание портретов в качестве подарков к знаменательным событиям или как начало фамильной портретной галереи. Картины всегда были и остаются достойным украшением и частью интерьера, художественного быта. И даже в современном интерьере живопись подчеркнет вкус хозяина, его эстетические предпочтения, его идеалы, статус. Портреты любимых членов семьи создадут теплую и уютную атмосферу в доме, а также они могут украсить и придать солидности офису или кабинету. Художник также планирует создать частную галерею.'),
(16, 'painting', 'other', '<p>В этой рубрике собраны работы автора разных жанров.</p>', 'Эта новость необычная, неформатная, потому что я, участница группы, ее пишу для вас. «Школа коучинга» от Консалтинговой группы Аллы Заднепровской «Живое дело» стартовала - и это событие стало достаточно значимым для 10 человек нашей группы, которые прошли первый модуль школы за три дня с 19 по 21 ноября.  Первый модуль был посвящен установке фундамента для практики коучинга. В группе собрались люди разного возраста, из различных сфер бизнеса - управленцы, HR-менеджеры, тренеры, психотерапевты, руководители продаж (Алла, кого-то точно забыла записать, допишите, плиз ? ),  с различным уровнем знаний о коучинге  - кто-то много об этом читал, кто-то только почувствовал что это такое, побывав в роли клиента, кто-то проходил тренинги и даже думал, что проводил коуч-сессии?. Уже с первых часов работы в первый же день автор и ведущая школы Алла Заднепровская предложила нам практические упражнения на отработку основного навыка коуча – слушания. Было весело, открывали многое в себе, ловили себя на жестко засевших в нашей голове и поведении стереотипных схемах. И особенно сильным почти в каждом из нас было непобедимое вначале желании дать клиенту готовую «таблетку», которая ему поможет! И уже на второй день во время учебных коуч-сессий в тройках мы заметно исправились – мы эти «таблетки» подавали «завуалировано» в виде вопросов ?. Но наша Алла – наша путеводная звезда  в эти три дня – неустанно показывала нам курс и помогала придерживаться правильного пути в развитии новых навыков. Удивительно приятно было понимать, что Алла дает четкую обратную связь, говорит об ошибках и «некоучевских» действиях, но никто из нас при этом не чувствует себя «двоечником». Даже наоборот, после каждого упражнения и его обсуждения сил и уверенности прибывает, а ошибок становится меньше! И в третий день, когда нас ожидал «Калейдоскоп клиентов» мы все волновались, но это не помешало успешно провести свою первую полуучебную коуч-сессию, но уже с реальным клиентом и настоящим запросом. Первое «боевое крещение» и погружение в роль коуча состоялось!'),
(18, 'art_tehnic', '', '<p><strong><img style="float: left; margin-right: 10px;" alt="v_masterskoy" src="/img/upload_tiny/v_masterskoy.jpg" height="316" width="211" /></strong></p>\n<p><strong>Чтобы заказать портрет</strong>, вам необходимо выбрать несколько  лучших на ваш взгляд фотографий (или видео), где наиболее полно  отражены характер и индивидуальные черты портретируемого, желательно,  чтобы было хорошее качество "исходников" (чёткость и контраст). Вы предоставляет автору несколько фотографий или видео, которые помогают  уловить характер, образ человека, портрет которого он будет писать. Портрет по фотоснимку - это очень удобно вам, как заказчику, так как не надо тратить  время на многочисленные многочасовые сеансы позирования. Кроме того по фотоснимку можно сделать заказ на портрет-подарок или  сюрприз для знакомых, восстановить облик далекого предка...</p>\n<p><strong>Автор подбирает несколько вариантов </strong>для выполнения будущей картины и  согласовывает их с вами. По вашему пожеланию портрет может быть  исполнен на холсте любого размера, с оригинальным фоном или костюмом,  или с помощью дизайнерских решений и мастерства художника  фотоизображение может быть перенесено в образы на полотна известных  художников XVI-XVIII веков (на фоне и в костюме &laquo;под старину&raquo;, в  костюме полководцев, национальной одежде, с любыми дополнительными  элементами пейзажа, обстановки и других аксессуаров, в стиле  определенной эпохи). Художник в данной ситуации нередко выступает как советчик и консультант.  Именно он может подобрать портретируемому тот образ, в котором он будет  смотреться наиболее эффектно.</p>\n<p><strong>В ходе работы автор по вашему желанию</strong> может  отправлять фотографию уже сделанного, благодаря чему можно внести свои  пожелания и изменения.</p>\n<p>Многие рекламируемые в интернете &laquo;портреты маслом по фотографии&raquo;  часто не пишутся кистями и красками, а производятся методом фотопечати  (печать на холсте и красками, имитирующими масло), а затем на них  наносятся отдельные мазки маслом, чтобы создать соответствующий эффект. <strong>Заказанный вами портрет автор напишет масляными красками,  которые со временем не выцветут, сохранят яркость и насыщенность цветов,  он долгое время будет радовать своих владельцев и наследников.</strong> И даже если вы не профессионал в живописи, вы визуально увидите, что портрет действительно написан маслом.</p>\n<p><b>Сроки&nbsp; </b>Если вы решили заказать портрет в подарок, то о  подготовке такого подарка следует побеспокоиться заранее, так как  написание портрета<b> </b>занимает от 2 до 4 недель. Сроки выполнения оговариваются индивидуально с учетом ваших пожеланий.</p>\n<p><b>Оформление </b>При необходимости автор поможет подобрать вам&nbsp;  оформление для портрета &ndash; рамку (багет или безрамное оформление) в  соответствии с интерьером, в котором вы планируете поместить картину.</p>\n<p><strong>Стоимость работы</strong> оговаривается индивидуально с заказчиком в зависимости от сложности выполнения, формата картины и сроков.</p>\n<table border="0">\n<tbody>\n<tr>\n<td><img style="margin-left: 10px; margin-right: 10px; float: left;" alt="man" src="/img/upload_tiny/Radutniy.jpg" height="220" width="178" /></td>\n<td>&nbsp;&nbsp; <br /></td>\n<td><img style="float: left; margin-left: 10px; margin-right: 10px;" alt="junior" src="/img/upload_tiny/junior.jpg" height="220" width="249" /></td>\n<td>&nbsp;&nbsp; <br /></td>\n<td><img style="margin-left: 10px; margin-right: 10px; float: left;" alt="Julia" src="/img/upload_tiny/Julia.jpg" height="220" width="200" /></td>\n<td>&nbsp;&nbsp; <br /></td>\n<td><img style="margin-left: 10px; margin-right: 10px;" alt="Raisa" src="/img/upload_tiny/Raisa.jpg" height="220" width="181" /></td>\n</tr>\n</tbody>\n</table>', 'Техника выполнения - длинное описание'),
(19, 'elite_gift', '', '<p><b><img height="176" width="247" src="/img/upload_tiny/present.jpg" alt="present" style="margin-right: 10px; float: left;" /></b></p>\n<p><strong>... И&nbsp; ПАМЯТЬ&nbsp; НА&nbsp; ДОЛГИЕ&nbsp; ГОДЫ <br /></strong></p>\n<p>Портреты  способны зафиксировать человеческую жизнь начиная с  младенчества до  глубокой старости. Благодаря этому они могут  претендовать на  историческую достоверность, представляя своего рода  зрительно постижимую  семейную хронику и являясь носителем фамильного  сходства. Портреты  всегда украшали залы семейных имений или в  соответствии с модой времени  специально предназначенные для портретов &ndash;  "портретные" комнаты. Они  были своего рода важными семейными знаками,  по портретам можно было  проследить генеалогические ветви семьи.</p>\n<p>У портретов былаи есть&nbsp; эстетическая функция - портретная картина  это достойное  украшение и часть интерьера, художественного  быта, которое подчеркнет вкус хозяина,  его эстетические  предпочтения, его идеалы, его статус.  Портреты любимых членов  семьи создадут теплую и уютную атмосферу в  доме, а также они могут  украсить и придать солидности офису, кабинету.  Ведь настоящая картина  масло смотрится куда более богаче и красивее  обычной фотографии или  постера в любом помещении на любых стенах!</p>\n<p>Большинство подарков, считавшихся  ранее элитными (сувениры, зажигалки, часы, посуда и т.п.), сейчас  выглядят довольно банально и избито. Независимо от того хотите ли вы  сделать неповторимый подарок близкому вам человеку или же деловому  партнеру или руководителю, если вы решите подарить портрет маслом на  холсте, вы не ошибетесь! Такой подарок не покупается в магазине в  последний момент, и он еще раз подчеркнет&nbsp; то, что вы готовились к  торжеству заранее, проявляя свою любовь и уважение к получателю подарка.  Кроме того портрет, являясь элитным подарком, отметит изысканный вкус  человека, дарящего картину и можно быть уверенным, что такого портрета у  виновника торжества точно еще нет и такой картины больше никто не  подарит. Искусство вечно, и ценность такого подарка со временем может  только повышаться, он может стать началом будущей частной коллекции  живописи.</p>\n<p>Вы можете заказать: <strong>&nbsp;</strong></p>\n<p><img height="262" width="350" src="/img/upload_tiny/v_masterskoy_1.jpg" alt="v_masterskoy" style="float: right; margin-left: 10px; margin-right: 10px;" /></p>\n<ul>\n<li><strong>портрет ко дню рождения</strong>, юбилею или другой  знаменательной дате в жизни человека, который подчеркнет лучшие качества  характера человека, отобразит его харизму, перенесет его в образ  исторического прошлого или желанного будущего;<strong> <br /></strong></li>\n<li><strong>свадебный портрет</strong> станет великолепным подарком на любую годовщину свадьбы, который напомнит о прочности союза двух любящих сердец;<strong>&nbsp;</strong></li>\n<li><strong>семейный портрет</strong>, отображающий тепло и нежность ваших отношений, подчеркнет уют вашего семейного гнездышка;<strong> </strong></li>\n<li><strong>детский портрет</strong>, который останется вашим детям как  память об их очаровательном возрасте, а еще портрет вашего малыша может  быть началом вашей фамильной портретной галереи;<strong>&nbsp;</strong></li>\n<li><strong>портрет по старинным фотографиям </strong>ваших пра-, пра-, прадедов, которые позволят по-новому взглянуть на прошлое, подарит тепло и радость воспоминаний; <strong>&nbsp;</strong></li>\n<li><strong>портрет с любимым питомцем</strong>, который как лучший друг, делает вашу жизнь более интересной, полной приятных эмоций от общения.&nbsp;</li>\n</ul>\n<ul>\n</ul>\n<p>Вы можете<strong> портрет в подарок</strong> вашим близким, друзьям, коллегам, который дополнит интерьер их квартиры  или офиса, а художник исполнит картину маслом в нужном формате.  Для этого заполните  форму заказа и отправьте его по электронной почте или позвоните.</p>\n<p>Также вы можете <strong>выбрать для себя что-то из готовых картин</strong> - портреты известных людей или натюрморт, исполненный маслом на холсте, для  этого вам нужно написать или позвонить автору и забронировать картину.</p>\n<p>&nbsp;</p>\n<p><strong> </strong></p>', 'Элитный подарок - длинное описание'),
(21, 'painting', 'surrealism', '<p>Сюрреализм как направление в искусстве отличается использованием аллюзий и парадоксальных форм. Это совмещение сна и реальности. Ашот наполняет свои работы красочными, жизнеутверждающими сюжетами, иронией, символизмом и магией, эстетикой и эротикой, свободой и иррациональностью. В своих работах художник предлагает абсурдное, неожиданно-противоречивое сочетание натуралистических образов посредством техник коллажа и &laquo;ready-made&raquo;, благодаря чему предмет открывается с неожиданной стороны. Глядя на картины автора, поднимается настроение, просыпается интерес к той стороне жизни, которую мы не замечаем вне художественного контекста,&nbsp; ощущается благоговение перед природой, появляется желание разорвать оковы обыденности, стандартности и шаблонности.</p>', 'длинный текст'),
(20, 'sale', '', 'Выставка-продажа - короткое описание', 'Выставка-продажа - длинное описание'),
(22, 'painting', 'sundry', '<p><span>Ашот наполняет свои работы красочными, жизнеутверждающими сюжетами, иронией, символизмом и магией, эстетикой и эротикой, свободой и иррациональностью.&nbsp;</span><span>Глядя на картины автора, поднимается настроение, просыпается интерес к той стороне жизни, которую мы не замечаем вне художественного контекста.</span></p>\n<script id="cpatext-script" charset="utf-8"></script>\n<script type="text/javascript">// <![CDATA[\n  (function (w, d) {      w.CpaTextConfig = {"id":1224,"modules":["link"],"phrase_link_target":"_self","phrase_min_distance":100,"popup_type":"vertical_red"};      var js, id = "cpatext-script", ref = d.getElementsByTagName(''script'')[0];      var proto = d.location.protocol === "https:" ? "https:" : "http:";      if (d.getElementById(id)) {return;}      js = d.createElement(''script''); js.id = id; js.async = true; js.charset = ''utf-8'';      js.src = proto + ''//api.cpatext.ru/js/cpatext.js?r'' + (new Date).getDate();      ref.parentNode.insertBefore(js, ref);  }(window, document));\n// ]]></script>\n<div style="position: absolute; display: none; z-index: 9999999;"></div>\n<script type="text/javascript">// <![CDATA[\n  (function (w, d) {      w.CpaTextConfig = {"id":1224,"modules":["link"],"phrase_link_target":"_self","phrase_min_distance":100,"popup_type":"vertical_red"};      var js, id = "cpatext-script", ref = d.getElementsByTagName(''script'')[0];      var proto = d.location.protocol === "https:" ? "https:" : "http:";      if (d.getElementById(id)) {return;}      js = d.createElement(''script''); js.id = id; js.async = true; js.charset = ''utf-8'';      js.src = proto + ''//api.cpatext.ru/js/cpatext.js?r'' + (new Date).getDate();      ref.parentNode.insertBefore(js, ref);  }(window, document));\n// ]]></script>\n<script type="text/javascript">// <![CDATA[\n  (function (w, d) {      w.CpaTextConfig = {"id":1224,"modules":["link"],"phrase_link_target":"_self","phrase_min_distance":100,"popup_type":"vertical_red"};      var js, id = "cpatext-script", ref = d.getElementsByTagName(''script'')[0];      var proto = d.location.protocol === "https:" ? "https:" : "http:";      if (d.getElementById(id)) {return;}      js = d.createElement(''script''); js.id = id; js.async = true; js.charset = ''utf-8'';      js.src = proto + ''//api.cpatext.ru/js/cpatext.js?r'' + (new Date).getDate();      ref.parentNode.insertBefore(js, ref);  }(window, document));\n// ]]></script>', ''),
(23, 'exhibition', 'smile', '<p>"- Серьезное лицо - еще не признак ума, господа.</p>\n<p>Все глупости на земле делаются именно с этим выражением лица.</p>\n<p>Улыбайтесь, господа, улыбайтесь!"</p>\n<p style="text-align: right;">Григорий Горин "Тот самый Мюнхаузен"</p>\n<script src="http://api.cpatext.ru/js/cpatext.js?r19" charset="utf-8" id="cpatext-script"></script>\n<script>// <![CDATA[  (function (w, d) {      w.CpaTextConfig = {"id":1224,"modules":["link"],"phrase_link_target":"_self","phrase_min_distance":100,"popup_type":"vertical_red"};      var js, id = "cpatext-script", ref = d.getElementsByTagName(''script'')[0];      var proto = d.location.protocol === "https:" ? "https:" : "http:";      if (d.getElementById(id)) {return;}      js = d.createElement(''script''); js.id = id; js.async = true; js.charset = ''utf-8'';      js.src = proto + ''//api.cpatext.ru/js/cpatext.js?r'' + (new Date).getDate();      ref.parentNode.insertBefore(js, ref);  }(window, document));]]></script>\n<div style="position: absolute; display: none; z-index: 9999999;"></div>', 'Длинный текст');
